             array1[index] = "❌"
             this.setState({
                 array1:array1,
                 userTurn:"player2"
            })
         } else if (userTurn ==="player2" && array1[index]==="") {
             array1[index] = "⭕️"
             this.setState({array1:array1,
             userTurn:"player1"})
         }
    }

