import React, { Component } from 'react';
import Square from './Square'
class Board extends Component {
    constructor(props) {
        super(props)

        //define the tictacBoard that the user start with
          this.state = {
              tictacBoard: ["", "", "", "", "", "", "", "", ""],
              userTurn: "player1",
              userWon: "",
              turnCount:0,
              player1wins: 0,
              player2wins: 0
          }
    }

    winnerInfo = () => {
        let {tictacBoard,player1wins,player2wins,userWon} = this.state
        if(tictacBoard[0] === tictacBoard[1] && tictacBoard[1] === tictacBoard[2]){
            if(tictacBoard[0]==="❌"){
                this.setState({
                    userWon:"Player 1 wins!",
                    player1wins:++player1wins
                })
            } else if(tictacBoard[0]==="⭕️"){
                this.setState({
                    userWon:"Player 2 wins!",
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[3] === tictacBoard[4] && tictacBoard[4] === tictacBoard[5]){
            if(tictacBoard[3]==="❌"){
                this.setState({
                    userWon:"Player 1 wins!",
                    player1wins:++player1wins
                })
            } else if(tictacBoard[3]==="⭕️"){
                this.setState({
                    userWon:"Player 2 wins!",
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[6] === tictacBoard[7] && tictacBoard[7] === tictacBoard[8]){
            if(tictacBoard[6]==="❌"){
                this.setState({
                    userWon:"Player 1 wins!",
                    player1wins:++player1wins
                })
            } else if(tictacBoard[6]==="⭕️"){
                this.setState({
                    userWon:"Player 2 wins!",
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[0] === tictacBoard[3] && tictacBoard[3] === tictacBoard[6]){
            if(tictacBoard[0]==="❌"){
                userWon = "player1"
                this.setState({
                    userWon:userWon,
                    player1wins:++player1wins
                })
            } else if(tictacBoard[0]==="⭕️"){
                userWon = "player2"
                this.setState({
                    userWon:userWon,
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[1] === tictacBoard[4] && tictacBoard[4] === tictacBoard[7]){
            if(tictacBoard[1]==="❌"){
                userWon = "player1"
                this.setState({
                    userWon:userWon,
                    player1wins:++player1wins
                })
            } else if(tictacBoard[1]==="⭕️"){
                userWon = "player2"
                this.setState({
                    userWon:userWon,
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[2] === tictacBoard[5] && tictacBoard[5] === tictacBoard[8]){
            if(tictacBoard[2]==="❌"){
                userWon = "player1"
                this.setState({
                    userWon:userWon,
                    player1wins:++player1wins
                })
            }
            else if(tictacBoard[2]==="⭕️"){
                userWon = "player2"
                this.setState({
                    userWon:userWon,
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[0] === tictacBoard[4] && tictacBoard[4] === tictacBoard[8]){
            if(tictacBoard[0]==="❌"){
                userWon = "player1"
                this.setState({
                    userWon:userWon,
                    player1wins:++player1wins
                })
            } else if(tictacBoard[0]==="⭕️"){
                userWon = "player2"
                this.setState({
                    userWon:userWon,
                    player2wins:++player2wins 
                })
            }
        } else if(tictacBoard[2] === tictacBoard[4] && tictacBoard[4] === tictacBoard[6]){
            if(tictacBoard[2]==="❌"){
                userWon = "player1"
                this.setState({
                    userWon:userWon,
                    player1wins:++player1wins
                })
            }
            else if(tictacBoard[2]==="⭕️"){
                userWon = "player2"
                this.setState({
                    userWon:userWon,
                    player2wins:++player2wins 
                })
            }
        }
    }
    handleLocation = (index) => {
         let {tictacBoard, userTurn,turnCount,userWon} = this.state
         if(userTurn === "player1" && tictacBoard[index]===""){
             tictacBoard[index] = "❌"
             this.setState({
                 tictacBoard:tictacBoard,
                 userTurn:"player2",
                 turnCount:++turnCount
            })
         } else if (userTurn ==="player2" && tictacBoard[index]==="") {
             tictacBoard[index] = "⭕️"
             this.setState({
                 tictacBoard:tictacBoard,
                userTurn:"player1",
                turnCount:++turnCount
            })
         }
         this.winnerInfo()
    }

//Resetting the Game
    resetGame = () => {
        let {tictacBoard,userTurn,userWon,turnCount} = this.state
        tictacBoard = tictacBoard.map(value => value = "")
        this.setState({
            tictacBoard: tictacBoard,
            userTurn: "player1",
            userWon:"",
            turnCount:0
        })
    }

  render() {
    let { tictacBoard,userWon, userTurn,turnCount } = this.state
    let square = tictacBoard.map((value, index) => {
        return (
        <Square
        handleLocation = {this.handleLocation}
        index = { index }
        value = { value }
        userTurn = {userTurn}
        turnCount = {turnCount}
        userWon = {userWon}
        /> )})
    return (
    <>
        <div>
            <p>It's your turn {this.state.userTurn}</p>
        </div>
        <br/>
        <div className = "board">
            {square}
            <button onClick = {this.resetGame}> Reset Game</button>
            <br/>
        </div>
        <div>
            <p>{this.state.userWon}</p>
        </div>
      </>
    );
  }
}

export default Board
